<#
.SYNOPSIS
    Test Password Complexity
.DESCRIPTION
    This script will test the password complexity for different factors. 
    (1) Will test that at least one capital letter was used.
    (2) Will test that at least one lowercase letter was used.
    (3) Will test that at least one number was used
    (4) Will test that the length of the password is between 12 and 24 chracters
    (5) Will test that no three characters are used consecutively
.EXAMPLE
    .\Test-Password.ps1
.OUTPUTS
    None
.NOTES
    Created by
        John Yoakum   2019-06-02
.REVISION HISTORY
    2019-06-02     1.0     Initial Script Creation

#>

##*=============================================
##* FUNCTION DECLARATION
##*=============================================
Function Test-Password()
{
# Function to create the reusable form for requesting the password.
Function CreateForm( [string[]]$Title,[string[]]$Text,[bool[]]$Password,[string[]]$base64 ) 
{
    
    Add-Type -AssemblyName System.Windows.Forms
    Add-Type -AssemblyName System.Drawing

    # Create the form object
    $form = New-Object System.Windows.Forms.Form
    $form.Text = $Title
    $form.Size = New-Object System.Drawing.Size(500,200)
    $form.StartPosition = 'CenterScreen'

    # Create the form icon
    $iconBytes       = [Convert]::FromBase64String($base64)
    $stream          = New-Object IO.MemoryStream($iconBytes, 0, $iconBytes.Length)
    $stream.Write($iconBytes, 0, $iconBytes.Length);
    $iconImage       = [System.Drawing.Image]::FromStream($stream, $true)
    $Form.Icon       = [System.Drawing.Icon]::FromHandle((New-Object System.Drawing.Bitmap -Argument $stream).GetHIcon())

    # Create the "Cancel" button
    $CancelButton = New-Object System.Windows.Forms.Button
    $CancelButton.Location = New-Object System.Drawing.Point(250,130)
    $CancelButton.Size = New-Object System.Drawing.Size(75,23)
    $CancelButton.Text = 'Cancel'
    $CancelButton.DialogResult = [System.Windows.Forms.DialogResult]::Cancel
    $form.CancelButton = $CancelButton
    $form.Controls.Add($CancelButton)

    # Create the 'OK' button
    $OKButton = New-Object System.Windows.Forms.Button
    $OKButton.Location = New-Object System.Drawing.Point(125,130)
    $OKButton.Size = New-Object System.Drawing.Size(100,23)
    $OKButton.Text = 'OK'
    $OKButton.DialogResult = [System.Windows.Forms.DialogResult]::OK
    $form.AcceptButton = $OKButton
    $form.Controls.Add($OKButton)

    # Create the top label for requesting the password
    $label = New-Object System.Windows.Forms.Label
    $label.Location = New-Object System.Drawing.Point(10,20)
    $label.Size = New-Object System.Drawing.Size(480,20)
    $label.Font = New-Object System.Drawing.Font("Arial", 9)
    $label.Text = $Text
    $form.Controls.Add($label)
    
    # I added the ability to not show the input box for the password 
    If ($Password) { 
    $textBox = New-Object System.Windows.Forms.TextBox
    $textBox.Location = New-Object System.Drawing.Point(10,40)
    $textBox.Size = New-Object System.Drawing.Size(460,20)
    $textBox.PasswordChar = '*'
    $form.Controls.Add($textBox)
    }

    # Set the first label that describes the password validation rules
    $label2 = New-Object System.Windows.Forms.Label
    $label2.Location = New-Object System.Drawing.Point(10,65)
    $label2.Size = New-Object System.Drawing.Size(480,20)
    $label2.Text = 'The password must be between 12 and 24 characters.'
    $label2.Font = New-Object System.Drawing.Font("Arial", 9)
    $form.Controls.Add($label2)

    # Set the second label that describes the password validation rules
    $label3 = New-Object System.Windows.Forms.Label
    $label3.Location = New-Object System.Drawing.Point(10,85)
    $label3.Size = New-Object System.Drawing.Size(480,20)
    $label3.Font = New-Object System.Drawing.Font("Arial", 9)
    $label3.Text = 'The password must contain one lowercase letter, one uppercase letter, one digit.'
    $form.Controls.Add($label3)

    # Set the third label that describes the password validation rules
    $label4 = New-Object System.Windows.Forms.Label
    $label4.Location = New-Object System.Drawing.Point(10,105)
    $label4.Size = New-Object System.Drawing.Size(480,20)
    $label4.Font = New-Object System.Drawing.Font("Arial", 9)
    $label4.Text = 'The password must NOT contain three repeating characters in a row ("...aaa...")'
    $form.Controls.Add($label4)

    # Show the form on top of everything else
    $form.Topmost = $true

    # If the Password box is shown, accept input for it
    If ($Password) { $form.Add_Shown({$textBox.Select()}) }
    
    # Show the form and accept the password
    $result = $form.ShowDialog()

    # If OK is pressed set the input to a variable
    if ($result -eq [System.Windows.Forms.DialogResult]::OK)
    {
        $x = $textBox.Text
    }
    # Return the input to the main script body
    Return $x
}

##*=============================================
##* VARIABLE DECLARATION
##*=============================================

# Intial Title of form
$TitleToPass = 'Yoakum Password Checker'
# Intial text message
$TextToPass = 'Please enter the password you wish to test:'
# Set the password box to be shown 
$ShowPasswordBox = $True
# Set that no successful password has be entered yet
$PasswordGood = $False
# Embedded images in base64 encoding (Successful Checkmark)
$CheckmarkImage = ""
# Embedded images in base64 encoding (Failure submitted)
$FailureImage = ""
# Set the initial icon of the form to the checkmark
$ShowIcon = $CheckmarkImage

##*=============================================
##* SCRIPT BODY
##*=============================================

# Process the results of the input until cancel or valid password
While ( $PasswordGood -ne $True )
{
    # Start the form function passing the variables to the function and saving the results as a variable
    $PasswordEntered = CreateForm $TitleToPass $TextToPass $ShowPasswordBox $ShowIcon
    
    # Start the process of validating the entry
    If ( $PasswordEntered.Length -ne 0) # Make sure that the entry has at least one character
        {
        if ($PasswordEntered.Length -lt 12 ) # Make sure that there are at least 12 characters enterered and return a result if not
        {
            $TitleToPass = 'Password Failed'
            $TextToPass = 'The Password you entered was too short, please try again.'
            $ShowPasswordBox = $True
            $PasswordGood = $False
            $ShowIcon = $FailureImage
        }
        elseif ($PasswordEntered.Length -gt 24) # Make sure that there are no more than 24 characters entered and return a result if so
        {
            $TitleToPass = 'Password Failed'
            $TextToPass = 'The Password you entered was too long, please try again.'
            $ShowPasswordBox = $True
            $PasswordGood = $False
            $ShowIcon = $FailureImage
        }
        elseif ( $PasswordEntered -cnotmatch '(?=.*[A-Z])' ) # Make sure that the entry has at least one capital letter
        {
            $TitleToPass = 'Password Failed'
            $TextToPass = 'The Password you entered did not include an uppercase letter, please try again.'
            $ShowPasswordBox = $True
            $PasswordGood = $False
            $ShowIcon = $FailureImage
        }
        elseif ( $PasswordEntered -cnotmatch '(?=.[a-z])' ) # make sure that the entry has at least one lowercase letter
        {
            $TitleToPass = 'Password Failed'
            $TextToPass = 'The Password you entered did not include an lowercase letter, please try again.'
            $ShowPasswordBox = $True
            $PasswordGood = $False
            $ShowIcon = $FailureImage
        }
        elseif ( $PasswordEntered -notmatch '(?=[^A-Za-z]*[A-Za-z])' ) # Make sure that there are valid alpha characters
        {
            $TitleToPass = 'Password Failed'
            $TextToPass = 'The Password you entered did not include any letters, please try again.'
            $ShowPasswordBox = $True
            $PasswordGood = $False
            $ShowIcon = $FailureImage
        }
        elseif ( $PasswordEntered -notmatch '(?:[^0-9]*[0-9])' ) # Make sure that there is at least one numeric value
        {
            $TitleToPass = 'Password Failed'
            $TextToPass = 'The Password you entered did not include any numbers, please try again.'
            $ShowPasswordBox = $True
            $PasswordGood = $False
            $ShowIcon = $FailureImage
        }
        elseif ( $PasswordEntered -match '([a-zA-Z\d])\1\1' ) # Make sure that there wasn't any characters repeated three times
        {
            $TitleToPass = 'Password Failed'
            $TextToPass = 'The Password you entered had a character three times in a row, please try again.'
            $ShowPasswordBox = $True
            $PasswordGood = $False
            $ShowIcon = $FailureImage
        }
        
        else # If meets all criteria, exit the verification loop
        {
            $PasswordGood = $True
            $NoEntry = $False
        }
        
    }
    elseif ( $PasswordEntered.Length -eq 0 ) # If no password entered, exit script
    {
        $PasswordGood = $True
        $NoEntry = $True
    }
    else # If successful password entered, set the password as successful and exit the while loop
    {
        $PasswordGood = $True
        $NoEntry = $False
    }

}

If ($NoEntry -eq $False) # If there was a password entered, show the successful form, if not, exit script
{
# Notify that they were successful

    Add-Type -AssemblyName System.Windows.Forms
    Add-Type -AssemblyName System.Drawing

    # Create the form object
    $form = New-Object System.Windows.Forms.Form
    $form.Text = 'Successful Password'
    $form.Size = New-Object System.Drawing.Size(500,200)
    $form.StartPosition = 'CenterScreen'

    # Create the icon image
    $iconBytes       = [Convert]::FromBase64String($CheckmarkImage)
    $stream          = New-Object IO.MemoryStream($iconBytes, 0, $iconBytes.Length)
    $stream.Write($iconBytes, 0, $iconBytes.Length);
    $iconImage       = [System.Drawing.Image]::FromStream($stream, $true)
    $Form.Icon       = [System.Drawing.Icon]::FromHandle((New-Object System.Drawing.Bitmap -Argument $stream).GetHIcon())

    # Create the form picture
    $pictureBox = new-object Windows.Forms.PictureBox
    $pictureBox.Width =  100
    $pictureBox.Height =  100
    $pictureBox.Location = New-Object System.Drawing.Size(10,20) 
    $pictureBox.Image = $iconImage
    $pictureBox.SizeMode = 4
    $form.Controls.Add($pictureBox)

    # Create the OK button
    $OKButton = New-Object System.Windows.Forms.Button
    $OKButton.Location = New-Object System.Drawing.Point(375,130)
    $OKButton.Size = New-Object System.Drawing.Size(100,23)
    $OKButton.Text = 'OK'
    $OKButton.DialogResult = [System.Windows.Forms.DialogResult]::OK
    $form.AcceptButton = $OKButton
    $form.Controls.Add($OKButton)

    # Create the label text
    $label = New-Object System.Windows.Forms.Label
    $label.Location = New-Object System.Drawing.Point(120,25)
    $label.MaximumSize = New-Object System.Drawing.Size(345, 110)
    $label.AutoSize = $True
    $label.Font = New-Object System.Drawing.Font("Arial", 16)
    $label.Text = 'Congratulations, You have successfully submitted a valid password.'
    $form.Controls.Add($label)

    # Set the form to be on top of everything else
    $form.Topmost = $true

    # Return the result of the OK button and exit script
    $result = $form.ShowDialog()
}

##*=============================================
##* END SCRIPT BODY
##*=============================================
}